import { useMemo } from 'react';
import { scaleLinear } from 'd3-scale';
import { useGetLabelsFromDataGroups } from './useGetLabelsFromDataGroups.js';

function useXScale({
  drawableWidth,
  data,
  xAxisOptions
}) {
  const labels = useGetLabelsFromDataGroups({
    data,
    xAxisOptions
  });
  const labelsLength = labels.length;
  const xScale = useMemo(() => {
    return scaleLinear().range([0, drawableWidth]).domain([0, labelsLength]);
  }, [drawableWidth, labelsLength]);
  return {
    xScale,
    labels
  };
}

export { useXScale };
