import { estimateStringWidth } from '@shopify/polaris-viz-core';

function getLongestTickWidth(ticks, characterWidths) {
  return ticks.reduce((previous, {
    formattedValue
  }) => {
    const width = estimateStringWidth(formattedValue, characterWidths);

    if (width > previous) {
      return width;
    }

    return previous;
  }, 0);
}

export { getLongestTickWidth };
