import { FONT_WEIGHT, NO_VALUE_WIDTH, ICON_SIZE, TEXT_ICON_SPACING, FONT_SIZE } from '../constants.js';
import { estimateStringWidthWithOffset } from '../../../utilities/estimateStringWidthWithOffset.js';

function estimateTrendIndicatorWidth(value, fontWeight = FONT_WEIGHT) {
  const textWidth = value ? estimateStringWidthWithOffset(value, FONT_SIZE, fontWeight) : NO_VALUE_WIDTH;
  const totalWidth = Math.round(ICON_SIZE + TEXT_ICON_SPACING + textWidth);
  return {
    totalWidth,
    textWidth
  };
}

export { estimateTrendIndicatorWidth };
